import {
  MemberManager
} from "./chunk-P5JGOPVX.js";
import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Moderation/ban.ts
init_esm_shims();
import { SlashCommandBuilder } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("ban").setDescription("Banne einen User").addUserOption((input) => input.setName("user").setDescription("Welchen User?").setRequired(true)).addStringOption((input) => input.setName("reason").setDescription("Der Grund f\xFCr den Ban").setRequired(true)).addNumberOption((input) => input.setName("deletemessageseconds").setDescription("In welcher Zeit vor dem Ban sollen die Nachrichten gel\xF6scht werden? (in Sekunden)")),
  async execute(interaction, client) {
    await interaction.deferReply({ ephemeral: true });
    const target = interaction.options.getMember("user");
    const deleteMessageSeconds = interaction.options.getNumber("deletemessageseconds") || void 0;
    const reason = interaction.options.getString("reason", true);
    const mod = interaction.member;
    const manager = new MemberManager(target, client.guild);
    const ban = await manager.ban(mod, reason, deleteMessageSeconds);
    if (!ban) return await interaction.editReply("Der Ban ist fehlgeschlagen!");
    else return await interaction.editReply(`Du hast ${target} erfolgreich gebannt!`);
  }
};
var ban_default = command;
export {
  ban_default as default
};
