import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Buttons/CustomEmbed/ce_color.ts
init_esm_shims();
import { ModalBuilder, TextInputBuilder, ActionRowBuilder } from "discord.js";
var ce_color_default = {
  id: "ce_color",
  async execute(interaction) {
    if (interaction.user.username != interaction.message.embeds[0].author.name) return interaction.reply({ content: "Du darfst an diesem Embed nichts \xE4ndern", ephemeral: true });
    const modal = new ModalBuilder({
      customId: "ce_color",
      title: "Farbe \xE4ndern"
    });
    const title = new TextInputBuilder({
      label: "Gib hier den Hex Color Code ein",
      maxLength: 7,
      minLength: 7,
      customId: "code",
      required: true,
      style: 1
    });
    const row = new ActionRowBuilder().addComponents(title);
    modal.addComponents(row);
    await interaction.showModal(modal);
  }
};
export {
  ce_color_default as default
};
