import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Admin/changestatus.ts
init_esm_shims();
import { SlashCommandBuilder, PermissionFlagsBits, ActivityType } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("changestatus").setDescription("\xC4ndere den Status des Bots").addStringOption((input) => input.setName("status").setDescription("Gib hier den Status ein, den der Bot haben soll").setRequired(true)).setDefaultMemberPermissions(PermissionFlagsBits.Administrator),
  async execute(interaction) {
    const customStatus = interaction.options.getString("status", true);
    interaction.client.user.setActivity({
      type: ActivityType.Custom,
      name: "customstatus",
      state: customStatus
    });
    interaction.reply({ content: `Der Status wurde erfolgreich auf **${customStatus}** gesetzt`, ephemeral: true });
  }
};
var changestatus_default = command;
export {
  changestatus_default as default
};
