import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Buttons/ticket/clan-close.ts
init_esm_shims();
import { ActionRowBuilder, ButtonBuilder, ButtonStyle, PermissionFlagsBits } from "discord.js";
var clan_close_default = {
  id: "clanticket-close",
  async execute(interaction) {
    if (!interaction.memberPermissions.has(PermissionFlagsBits.Administrator)) return interaction.reply({ content: "Du darfst dieses Ticket nicht schlie\xDFen", ephemeral: true });
    const row = new ActionRowBuilder({
      components: [
        new ButtonBuilder({
          customId: "close-confirm",
          label: "Ticket schlie\xDFen",
          style: ButtonStyle.Danger
        }),
        new ButtonBuilder({
          customId: "close-decline",
          label: "Offen lassen",
          style: ButtonStyle.Secondary
        })
      ]
    });
    await interaction.reply({ content: "Sicher, dass du das Ticket schlie\xDFen m\xF6chtest?", components: [row] });
  }
};
export {
  clan_close_default as default
};
