import {
  warns_default
} from "./chunk-OPC27YAZ.js";
import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Moderation/clearwarnings.ts
init_esm_shims();
import { SlashCommandBuilder, PermissionFlagsBits } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("clearwarnings").setDescription("L\xF6sche alle Verwarnungen von einem User").addUserOption((input) => input.setName("user").setDescription("Welcher User?").setRequired(true)).setDefaultMemberPermissions(PermissionFlagsBits.Administrator),
  async execute(interaction, client) {
    const target = interaction.options.getMember("user");
    await warns_default.findOne({ userId: target.user.id }).then(async (User) => {
      if (!User || User.warns.length == 0) {
        interaction.reply({ content: `${target} hat keine Verwarnungen!`, ephemeral: true });
        return;
      } else {
        User.warns = [];
        await User.save();
        interaction.reply({ content: `Du hats alle Verwarnungen von ${target} gel\xF6scht!`, ephemeral: true });
      }
    }).catch(console.log);
  }
};
var clearwarnings_default = command;
export {
  clearwarnings_default as default
};
