import {
  MemberManager
} from "./chunk-P5JGOPVX.js";
import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Moderation/kick.ts
init_esm_shims();
import { SlashCommandBuilder, PermissionFlagsBits } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("kick").setDescription("Kicke einen User").addUserOption((input) => input.setName("user").setDescription("Welcher User?").setRequired(true)).addStringOption((input) => input.setName("reason").setDescription("Grund f\xFCr den Kick").setRequired(true)).setDefaultMemberPermissions(PermissionFlagsBits.KickMembers),
  async execute(interaction, client) {
    const member = interaction.options.getMember("user");
    const guild = interaction.guild;
    const reason = interaction.options.getString("reason", true);
    const mod = interaction.member;
    if (member.roles.highest.position >= mod.roles.highest.position) return interaction.reply({ content: "Du kannst diesen User nicht kicken, weil seine h\xF6chste Rolle h\xF6her oder gleich deiner h\xF6chsten Rolle ist", ephemeral: true });
    const Member = new MemberManager(member, guild);
    const kick = await Member.kick(mod, reason);
    if (!kick) return interaction.reply({ content: "Etwas ist schiefgelaufen", ephemeral: true });
    else return interaction.reply({ content: `Du hast ${member.user.username} erfolgreich gekickt`, ephemeral: true });
  }
};
var kick_default = command;
export {
  kick_default as default
};
