import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Admin/logout.ts
init_esm_shims();
import { SlashCommandBuilder, PermissionFlagsBits, ActivityType } from "discord.js";
import { ConsoleWarning } from "contents";
var cw = new ConsoleWarning();
var command = {
  data: new SlashCommandBuilder().setName("stop").setDescription("Stoppe den Bot").setDefaultMemberPermissions(PermissionFlagsBits.Administrator),
  async execute(interaction, client) {
    interaction.reply({ content: "Der Bot wird gestoppt", ephemeral: true });
    client.user.setActivity({
      name: "offline",
      state: "Bot Offline",
      type: ActivityType.Custom
    });
    await client.destroy();
    cw.show(`${interaction.user.username} (${interaction.user.id}) hat den Bot gestoppt!`);
  }
};
var logout_default = command;
export {
  logout_default as default
};
