import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Admin/ticket.ts
init_esm_shims();
import { StringSelectMenuBuilder, SlashCommandBuilder, Colors, ActionRowBuilder, StringSelectMenuOptionBuilder, EmbedBuilder, PermissionFlagsBits } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("ticket").setDescription("Sende das Ticket Embed in den Channel").setDefaultMemberPermissions(PermissionFlagsBits.Administrator),
  async execute(interaction) {
    const channel = interaction.channel;
    const select = new StringSelectMenuBuilder().setCustomId("ticket").setPlaceholder("W\xE4hle ein Thema f\xFCr ein Ticket...").addOptions(
      new StringSelectMenuOptionBuilder().setLabel("Bewerben").setDescription("Du m\xF6chtest dich bewerben?").setEmoji("\u{1F91D}").setValue("bewerben"),
      new StringSelectMenuOptionBuilder().setLabel("Partnerschaft").setDescription("Du m\xF6chtest eine Partnerschaft beginnen?").setEmoji("\u{1F91D}").setValue("partnerschaft"),
      new StringSelectMenuOptionBuilder().setLabel("Report").setDescription("Du m\xF6chtest einen User melden?").setEmoji("\u{1F4A2}").setValue("report"),
      new StringSelectMenuOptionBuilder().setLabel("Support").setDescription("Du brauchst hilfe?").setEmoji("\u{1F4E9}").setValue("support")
    );
    const row = new ActionRowBuilder().addComponents(select);
    const embed = new EmbedBuilder({
      title: "Willkommen im Support! Erstelle einfach ein Ticket",
      description: "Bn\xF6tigst du Hilfe, m\xF6chtest du dich bewerben oder m\xF6chtest du ein Mitglied mellden?\nDann bist du hier genau richtig!\nErstelle einfach ein Ticket und beantworte die Fragen ehrlich\nUnser Serverteam steht bereit um dir zu helfen",
      color: Colors.Blue
    });
    await channel.send({ embeds: [embed], components: [row] });
    await interaction.reply({ content: ":thumbsup:", ephemeral: true });
  }
};
var ticket_default = command;
export {
  ticket_default as default
};
