import {
  warns_default
} from "./chunk-OPC27YAZ.js";
import {
  init_esm_shims
} from "./chunk-WWCIODDN.js";

// Commands/Moderation/warns.ts
init_esm_shims();
import { SlashCommandBuilder, EmbedBuilder, PermissionFlagsBits, Colors } from "discord.js";
var command = {
  data: new SlashCommandBuilder().setName("warns").setDescription("Lasse dir alle Warns von einem User anzeigen").addUserOption((input) => input.setName("user").setDescription("Der User, von dem du die Warns sehen m\xF6chtest").setRequired(true)).setDefaultMemberPermissions(PermissionFlagsBits.ModerateMembers),
  async execute(interaction) {
    const member = interaction.options.getMember("user");
    await warns_default.findOne({ userId: member.user.id }).then(async (User) => {
      if (!User || User.warns.length == 0) {
        interaction.reply({ content: `${member} hat keine Verwarnungen!`, ephemeral: true });
      } else {
        const embed = new EmbedBuilder({
          title: `Warns von ${member.displayName}`,
          description: "",
          color: Colors.Gold
        });
        for (const warn of User.warns) {
          embed.data.description += `${warn.date} von ${warn.moderator}
Grund: **${warn.reason}**
Warn-ID: \`${warn.id}\`

`;
        }
        interaction.reply({ embeds: [embed] });
        return;
      }
    }).catch(console.log);
  }
};
var warns_default2 = command;
export {
  warns_default2 as default
};
